mapper.select:
SELECT DISTINCT m.* FROM cms_user_role AS ur
LEFT JOIN cms_role AS r ON ur.role_id = r.id
LEFT JOIN cms_role_menu AS rm ON r.id = rm.role_id
LEFT JOIN cms_menu AS m ON rm.menu_id = m.id
WHERE ur.user_id = #{userId}
ORDER BY m.order_num ASC;

SELECT m.* FROM cms_role_menu AS rm, cms_menu AS m
WHERE rm.menu_id = m.id
AND rm.role_id = #{roleId}
